import { createApp } from 'vue'
import App from './App.vue'

createApp(App).mount('#App')


import axios from "axios";

export default {
name:'TodoApp',
  data() {
    return {
      todos: [],
      newTodo: "",
      error: "",
    };
  },
  mounted() {
    this.getTodos();
  },
  methods: {
    async getTodos() {
      try {
        const res = await axios.get("http://localhost:3000/api/");
        this.todos = res.data;
      } catch (err) {
        this.error = "Could not fetch todos";
      }
    },
    async addTodo() {
      if (!this.newTodo.trim()) return;
      try {
        const res = await axios.post("http://localhost:3000/api/", {
          text: this.newTodo,
          completed: false,
        });
        this.todos.push(res.data);
        this.newTodo = "";
      } catch (err) {
        this.error = "Failed to add todo";
      }
    },
    async deleteTodo(id) {
      try {
        await axios.delete(`http://localhost:3000/api/${id}`);
        this.todos = this.todos.filter((todo) => todo._id !== id);
      } catch (err) {
        this.error = "Failed to delete todo";
      }
    },
    async toggleTodo(todo) {
      try {
        await axios.put(`http://localhost:3000/api/${todo._id}`, {
          completed: todo.completed,
        });
      } catch (err) {
        this.error = "Failed to update todo";
      }
    },
    editTodo(todo) {
      const updatedText = prompt("Edit your todo:", todo.text);
      if (updatedText && updatedText.trim()) {
        axios
          .put(`http://localhost:3000/api/${todo._id}`, {
            text: updatedText.trim(),
            completed: todo.completed,
          })
          .then((res) => {
            todo.text = res.data.text;
          })
          .catch(() => {
            this.error = "Failed to update todo text";
          });
      }
    },
  },
};
